mapper.select:
    select *
	from(
        select rownum rn, a.*
        from ( 
                 select *
                from tbl_board order by brd_no desc
                ) a
            )b
	where b.rn > (#{page} - 1) * 10 
	
	and     b.rn <= #{page} * 10

mapper.update:
update tbl_board
set		brd_title = #{brdTitle}
		,brd_content = #{brdContent}
where	brd_no = #{brdNo}
